// src/App.js
import React, { useState } from 'react';
import jsPDF from 'jspdf';
import { saveAs } from 'file-saver';
import { Document, Packer, Paragraph, TextRun } from 'docx';

const App = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const handlePDFDownload = () => {
    const doc = new jsPDF();
    doc.text(`Name: ${name}`, 10, 10);
    doc.text(`Email: ${email}`, 10, 20);
    doc.save('letter.pdf');
  };

  const handleDOCDownload = async () => {
    const doc = new Document({
      sections: [
        {
          children: [
            new Paragraph({
              children: [new TextRun(`Name: ${name}`)],
            }),
            new Paragraph({
              children: [new TextRun(`Email: ${email}`)],
            }),
          ],
        },
      ],
    });

    const blob = await Packer.toBlob(doc);
    saveAs(blob, 'letter.docx');
  };

  return (
    <div style={{ width: '210mm', height: '297mm', padding: '20mm', border: '1px solid black' }}>
      <h1>A4 Letter Page</h1>
      <form>
        <div>
          <label>Name: </label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div>
          <label>Email: </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
      </form>
      <button onClick={handlePDFDownload}>Download as PDF</button>
      <button onClick={handleDOCDownload}>Download as DOC</button>
    </div>
  );
};

export default App;
