import React, { useState } from 'react';
import { saveAs } from 'file-saver';
import { Document, Packer, Paragraph, TextRun, ExternalHyperlink } from 'docx';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import './App.css';

const App = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [website, setWebsite] = useState('');
  const [linkedin, setLinkedin] = useState('');
  const [github, setGithub] = useState('');

  const handlePDFDownload = () => {
    const letterBox = document.querySelector('.letter-box');
    html2canvas(letterBox).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      pdf.addImage(imgData, 'PNG', 0, 0, 210, 297);
      pdf.save('resume.pdf');
    });
  };

  const handleDOCDownload = async () => {
    const doc = new Document({
      sections: [
        {
          children: [
            new Paragraph({
              children: [new TextRun(`Name: ${name}`)],
            }),
            new Paragraph({
              children: [new TextRun(`Email: ${email}`)],
            }),
            new Paragraph({
              children: [new TextRun(`Phone: ${phone}`)],
            }),
            new Paragraph({
              children: [
                new TextRun('Website: '),
                new ExternalHyperlink({
                  link: website,
                  children: [new TextRun({ text: website, style: 'Hyperlink' })],
                }),
              ],
            }),
            new Paragraph({
              children: [
                new TextRun('LinkedIn: '),
                new ExternalHyperlink({
                  link: linkedin,
                  children: [new TextRun({ text: linkedin, style: 'Hyperlink' })],
                }),
              ],
            }),
            new Paragraph({
              children: [
                new TextRun('GitHub: '),
                new ExternalHyperlink({
                  link: github,
                  children: [new TextRun({ text: github, style: 'Hyperlink' })],
                }),
              ],
            }),
          ],
        },
      ],
    });

    const blob = await Packer.toBlob(doc);
    saveAs(blob, 'resume.docx');
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h1>Resume Builder</h1>
        <form>
          <div>
            <label>Name: </label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div>
            <label>Email: </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label>Phone: </label>
            <input
              type="tel"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>
          <div>
            <label>Portfolio Website: </label>
            <input
              type="url"
              value={website}
              onChange={(e) => setWebsite(e.target.value)}
            />
          </div>
          <div>
            <label>LinkedIn: </label>
            <input
              type="url"
              value={linkedin}
              onChange={(e) => setLinkedin(e.target.value)}
            />
          </div>
          <div>
            <label>GitHub: </label>
            <input
              type="url"
              value={github}
              onChange={(e) => setGithub(e.target.value)}
            />
          </div>
        </form>
        <div className="buttons-container">
          <button onClick={handlePDFDownload}>Download as PDF</button>
          <button onClick={handleDOCDownload}>Download as DOC</button>
        </div>
      </div>
      <div className="letter-box-container">
        <div className="letter-box">
          <h1>A4 Resume Page</h1>
          <p>Name: {name}</p>
          <p>Email: {email}</p>
          <p>Phone: {phone}</p>
          <p>Website: <a href={website} target="_blank" rel="noopener noreferrer">{website}</a></p>
          <p>LinkedIn: <a href={linkedin} target="_blank" rel="noopener noreferrer">{linkedin}</a></p>
          <p>GitHub: <a href={github} target="_blank" rel="noopener noreferrer">{github}</a></p>
        </div>
      </div>
    </div>
  );
};

export default App;
